import React, { useState, useRef, useEffect } from 'react';
import './style.css'; 


function VideoPlayer() {
  const [showPopup, setShowPopup] = useState(false);
  const videoRef = useRef(null);

  function handleTimeUpdate(event) {
    const time = Math.floor(event.target.currentTime);

    if (time > 0 && time % 120 === 0) {
      setShowPopup(true);
    }

    if (time > 0 && time % 300 === 0) {
      setShowPopup(true);
      videoRef.current.pause();
    }
  }

  function handlePopupClose() {
    setShowPopup(false);
    videoRef.current.play();
  }

  useEffect(() => {
    videoRef.current.addEventListener('timeupdate', handleTimeUpdate);
    return () => {
      videoRef.current.removeEventListener('timeupdate', handleTimeUpdate);
    };
  }, []);

  return (
    <div className="video-container">
      <video ref={videoRef} className="video-player" controls>
        <source
          src=""
          // type="video/mp4"
        />
      </video>
      {showPopup && (
        <div className="popup">
          <p className="popup-text">
            {videoRef.current.currentTime < videoRef.current.duration
              ? 'Congratulations! You have completed 2 minutes of the video.'
              : 'Congratulations! You have completed 5 minutes of the video.'}
          </p>
          <button className="popup-close-btn" onClick={handlePopupClose}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default VideoPlayer;
