import React, { useState } from "react";

const Product = () => {
  const [products, setProducts] = useState([
    { name: "Product 1", price: 10, quantity: 1 },
    { name: "Product 2", price: 20, quantity: 1 },
    { name: "Product 3", price: 30, quantity: 1 },
  ]);

  const [totalPrice, setTotalPrice] = useState(60);
  const [totalQuantity, setTotalQuantity] = useState(3);

  const removeProduct = (index) => {
    const newProducts = [...products];
    const productToRemove = newProducts[index];
    setTotalPrice(totalPrice - productToRemove.price);
    setTotalQuantity(totalQuantity - productToRemove.quantity);
    newProducts.splice(index, 1);
    setProducts(newProducts);
  };

  const addProduct = (index) => {
    const newProducts = [...products];
    const productToAdd = newProducts[index];
    setTotalPrice(totalPrice + productToAdd.price);
    setTotalQuantity(totalQuantity + productToAdd.quantity);
    newProducts.splice(index + 1, 0, productToAdd);
    setProducts(newProducts);
  };

  return (
    <div>
      <h2>Product List</h2>
      <ul>
        {products.map((product, index) => (
          <li key={index}>
            {product.name} ({product.price} x {product.quantity}){" "}
            <button onClick={() => removeProduct(index)}>Remove</button>{" "}
            <button onClick={() => addProduct(index)}>Add</button>
          </li>
        ))}
      </ul>
      <p>Total Price: {totalPrice}</p>
      <p>Total Quantity: {totalQuantity}</p>
    </div>
  );
};

export default Product;
