import React from "react";

class Formula extends React.Component{
        constructor(props){
                super(props);
                this.state={
                        a:12, 
                        b:30, 
                        c:25, 
                        d:56
                 };
        }
        changeVal=()=>{
                this.setState({a:"10"}); 
                // this.setState({b:"5", c:"2", d:"6"});

        }
        render(){

                return(
                        <>
                        <button onClick={this.changeVal}>Click Here</button>
                        <h1>{this.state.a + this.state.b + this.state.c + this.state.d }</h1>
                        </>
                );
        }

}

export default Formula;
