import React from "react";

class Formula extends React.Component{
        constructor(props){
                super(props);
                this.state={
                        a:12, 
                        b:30, 
                        c:25, 
                        d:56
                 };
        }

        changeVal=()=>{
                // this.setState({a:"10"}); 
                this.setState({b:"5", c:"2", d:"6"});

        }

        changeVals=()=>{

                this.setState({a:"9", b:"16", c:"2", d:"6"});

        }

        changeValss=()=>{

            this.setState({a:"2", b:"2"});

        }

        render(){

            var sum = this.state.a + this.state.b + this.state.c + this.state.d ;

                return(
                        <>
                        <button onClick={this.changeVal}>Click Here</button>
                        <h1>{sum}</h1>


                        <button onClick={this.changeVals}>Click Here</button>
                        <h1>Sum: {this.state.a * this.state.b * this.state.c * this.state.d }</h1>

                        <button onClick={this.changeValss}>Click Here</button>
                        <h1>(a+b)^2: {this.state.a * this.state.a + 2*this.state.a*this.state.b + this.state.b * this.state.b}</h1>


                        </>
                );
        }

}

export default Formula;
